  const legendData = [
    { label: "Ekstrim", color: "#7C1B13" },
    { label: "Sangat Tinggi", color: "#B82C21" },
    { label: "Tinggi", color: "#F44336" },
    { label: "Sedang", color: "#F68D8A" },
    { label: "Rendah", color: "#FAC6C4" },
  ];

  const handleToggle = () => {
    if (openLegend) {
      setIsClosing(true);
      setTimeout(() => {
        setOpenLegend(false);
        setIsClosing(false);
      }, 300);
    } else {
      setOpenLegend(true);
    }
  };

  const handleMouseMove = (e: React.MouseEvent<HTMLDivElement>) => {
    setMousePosition({ x: e.clientX, y: e.clientY });
  };

  const smoothScrollTo = (target: string) => {
    gsap.to(window, {
      duration: 1.5,
      scrollTo: target,
      ease: "power2.inOut",
    });
  };

  useEffect(() => {
    if (hoveredIsland) {
      const targetPercentage = islandData[hoveredIsland] || 0;
      let currentPercentage = 0;
      const increment = targetPercentage / 30;

      const timer = setInterval(() => {
        currentPercentage += increment;
        if (currentPercentage >= targetPercentage) {
          setAnimatedPercentage(targetPercentage);
          clearInterval(timer);
        } else {
          setAnimatedPercentage(Math.floor(currentPercentage));
        }
      }, 16);

      return () => clearInterval(timer);
    } else {
      setAnimatedPercentage(0);
    }
  }, [hoveredIsland]);

  const getIslandColor = (islandId: string) => {
    const percentage = islandData[islandId];
    if (percentage >= 85) return "#7C1B13";
    if (percentage >= 70) return "#B82C21";
    if (percentage >= 55) return "#F44336";
    if (percentage >= 40) return "#F68D8A";
    return "#FAC6C4";
  };

  return (
    <>
  <style>{`
    @keyframes slideUp {
      from { opacity: 0; transform: translateY(10px); }
      to { opacity: 1; transform: translateY(0); }
    }
    
    @keyframes slideDown {
      from { opacity: 1; transform: translateY(0); }
      to { opacity: 0; transform: translateY(10px); }
    }
    
    @keyframes fadeIn {
      from { opacity: 0; }
      to { opacity: 1; }
    }
    
    @keyframes fadeOut {
      from { opacity: 1; }
      to { opacity: 0; }
    }
  `}</style>

  <div
    className="relative min-h-screen bg-[#0A0F1F] flex items-center justify-center p-8"
    onMouseMove={handleMouseMove}
  >
    <div className="absolute bottom-0 left-0 w-full h-80 bg-gradient-to-t from-[#737373]/40 to-transparent"></div>
    <div className="w-full max-w-7xl">
      <div className="flex flex-col lg:flex-row items-start justify-between gap-8 h-full">
        <div className="text-white lg:w-1/4 relative h-[36rem] flex flex-col justify-end">
          <div className="flex items-center gap-3">
            <button
              onClick={handleToggle}
              className="w-10 h-10 rounded-md flex items-center justify-center hover:opacity-80 cursor-pointer bg-[#D9D9D9]"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className={`w-6 h-6 transition-transform ${
                  openLegend ? "rotate-180" : "rotate-0"
                }`}
                fill="none"
                viewBox="0 0 24 24"
                stroke="#1D1B20"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 9l-7 7-7-7"
                />
              </svg>
            </button>

            <div className="bg-[#0A0F1F] backdrop-blur shadow text-lg text-white flex items-center justify-center h-10 rounded-md px-4">
              Source from Google Trend
            </div>
          </div>

          {(openLegend || isClosing) && (
            <div
              className="absolute bottom-14 flex items-start gap-2"
              style={{
                animation: isClosing
                  ? "slideDown 0.3s ease-out"
                  : "slideUp 0.3s ease-out",
              }}
            >
              <div className="flex flex-col items-center rounded bg-[rgba(217,217,217,0.12)] w-10 p-2 gap-2">
                {legendData.map((item, idx) => (
                  <div
                    key={idx}
                    className="w-6 h-6 rounded"
                    style={{
                      backgroundColor: item.color,
                      animation: isClosing
                        ? `fadeOut 0.2s ease-out ${
                            (legendData.length - idx - 1) * 0.03
                          }s both`
                        : `fadeIn 0.3s ease-out ${idx * 0.05}s both`,
                    }}
                  />
                ))}
              </div>

              <div className="flex flex-col py-2 gap-2">
                {legendData.map((item, idx) => (
                  <div
                    key={idx}
                    className="flex items-center h-6"
                    style={{
                      animation: isClosing
                        ? `fadeOut 0.2s ease-out ${
                            (legendData.length - idx - 1) * 0.03
                          }s both`
                        : `fadeIn 0.3s ease-out ${idx * 0.05}s both`,
                    }}
                  >
                    <span className="text-sm">{item.label}</span>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        <div className="flex-1 relative">
          <svg
            viewBox="1000 0 4000 2000"
            preserveAspectRatio="xMidYMid meet"
            className="w-full h-auto max-h-[600px]"
            overflow="visible"
          >
            {islands.map((island, index) => {
              const transforms = [
                "translate(-1200, 800) scale(5.5)",
                "translate(700, 2700) scale(5.5)",
                "translate(1200, 600) scale(6)",
                "translate(3300, 1100) scale(5.5)",
                "translate(2400, 3000) scale(5.5)",
                "translate(2650, 2980) scale(6)",
                "translate(3300, 2900) scale(6)",
                "translate(4300, 900) scale(5)",
                "translate(5200, 1200) scale(6)",
              ];

              return (
                <g key={island.id} transform={transforms[index]}>
                  <path
                    d={island.path}
                    fill={
                      hoveredIsland === island.id
                        ? getIslandColor(island.id)
                        : "#222C4D"
                    }
                    stroke="#B9D86D"
                    strokeWidth="2"
                    className="transition-all duration-300 cursor-pointer"
                    onMouseEnter={() => setHoveredIsland(island.id)}
                    onMouseLeave={() => setHoveredIsland(null)}
                    style={{
                      filter:
                        hoveredIsland === island.id
                          ? "brightness(1.05) drop-shadow(0 0 10px rgba(34,211,238,0.3))"
                          : "none",
                    }}
                  >
                    <title>{island.name}</title>
                  </path>
                </g>
              );
            })}
          </svg>

          {hoveredIsland && (
            <div
              className="fixed pointer-events-none z-50"
              style={{
                left: `${mousePosition.x + 20}px`,
                top: `${mousePosition.y - 20}px`,
                transform: "translate(0, -100%)",
              }}
            >
              <div className="bg-transparent rounded-lg flex flex-col items-center gap-2 p-[15px_20px] min-w-[100px] border border-[#DFE3F2]">
                <p className="text-white font-bold text-lg text-center">
                  {islands.find((i) => i.id === hoveredIsland)?.name}
                </p>

                <div className="flex flex-col items-center gap-3">
                  <div className="relative w-20 h-20">
                    <svg className="w-20 h-20 transform -rotate-90">
                      <circle
                        cx="40"
                        cy="40"
                        r="36"
                        stroke="#e5e7eb"
                        strokeWidth="5"
                        fill="none"
                      />
                      <circle
                        cx="40"
                        cy="40"
                        r="36"
                        stroke="#F44336"
                        strokeWidth="5"
                        fill="none"
                        strokeDasharray={`${2 * Math.PI * 36}`}
                        strokeDashoffset={`${
                          2 * Math.PI * 36 * (1 - animatedPercentage / 100)
                        }`}
                        className="transition-all duration-300"
                        strokeLinecap="round"
                      />
                    </svg>
                    <div className="absolute inset-0 flex items-center justify-center">
                      <span className="text-white font-bold text-lg">
                        {animatedPercentage}%
                      </span>
                    </div>
                  </div>

                  <p className="text-white text-sm text-center leading-tight">
                    Pemain Judi Online
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>

      <div className="lg:w-1/4 text-right relative">
        <h1 className="absolute top-4 text-4xl text-right">
          A Closer Look at
          <br />
          Online Gambling in Indonesia
        </h1>
      </div>

      </div>
    </div>
  </div>
</>
  );
